sqlMap.select:
/*mesStatisticsDAO.selectAssetMcList*/ WITH dateTemplate AS ( SELECT CONCAT(e_year,'-', e_month, '-', e_date) AS eDate FROM e_tmpyear, e_month, e_date WHERE 1=1 AND CONCAT(e_year,'-', e_month, '-', e_date) BETWEEN #topStartDate# AND #topEndDate# ORDER BY e_year, e_month, e_date ) SELECT d.eDate AS eDate , ASSET_MAKER AS sAssetMaker , COUNT(A.ASSET_NUM) AS sAssetCount FROM dateTemplate d LEFT JOIN A_ASSET A ON (DATE_FORMAT(ASSET_DATE, '%Y-%m-%d') = d.eDate) WHERE A.ASSET_MAKER IS NOT NULL AND A.ASSET_MAKER != '' AND A.ASSET_DEL_STATUS != '삭제'
AND A.ASSET_STATUS = #searchType#
GROUP BY A.ASSET_MAKER ORDER BY d.eDate ASC;

/*mesStatisticsDAO.selectIEList*/ WITH dateTemplate AS ( SELECT CONCAT(e_year,'-', e_month, '-', e_date) AS eDate FROM e_tmpyear, e_month, e_date WHERE 1=1 AND CONCAT(e_year,'-', e_month, '-', e_date) BETWEEN #topStartDate# AND #topEndDate# ORDER BY e_year, e_month, e_date ) SELECT d.eDate , IF(ASSET_TYPE_NAME='' OR ASSET_TYPE_NAME IS NULL ,'자산유형누락',ASSET_TYPE_NAME) AS sAssetMaker , ASSET_TYPE AS sAssetUesType , COUNT(A.ASSET_NUM) AS sAssetCount FROM dateTemplate d LEFT JOIN A_ASSET A ON (DATE_FORMAT(ASSET_DATE, '%Y-%m-%d') = d.eDate) WHERE A.ASSET_MAKER IS NOT NULL AND A.ASSET_MAKER != '' AND A.ASSET_DEL_STATUS != '삭제'
AND A.ASSET_STATUS = #searchType#
GROUP BY A.ASSET_TYPE ORDER BY d.eDate ASC;

/*mesStatisticsDAO.secondValuList*/ SELECT SUM(eValueA) AS eValueB,eWordA AS eWordB FROM ( SELECT COUNT(aa.ASSET_TYPE) AS eValueA, aa.ASSET_TYPE_NAME AS eWordA FROM I_ISSUE_ASSET as iia LEFT JOIN I_ISSUE AS ii on(iia.ISSUE_NUM = ii.ISSUE_NUM) LEFT JOIN A_ASSET AS aa on(aa.ASSET_NUM = iia.ASSET_NUM) WHERE ii.eIssueStatus != '삭제' AND aa.ASSET_TYPE IS NOT NULL AND DATE_FORMAT(ii.eRequestDate, '%Y-%m-%d') BETWEEN #topStartDate# AND #topEndDate# GROUP BY aa.ASSET_TYPE UNION ALL
SELECT COUNT(aa.ASSET_TYPE) AS eValueA, aa.ASSET_TYPE_NAME AS eWordA FROM I_CHANGE_ASSET as ica LEFT JOIN I_CHANGE AS ic on(ica.eChangeKey =ic.eChangeKey) LEFT JOIN A_ASSET AS aa on(aa.ASSET_NUM = ica.eAssetKey) WHERE ic.eStatus != '삭제' AND aa.ASSET_TYPE IS NOT NULL AND DATE_FORMAT(ic.eReqDate, '%Y-%m-%d') BETWEEN #topStartDate# AND #topEndDate# GROUP BY aa.ASSET_TYPE UNION ALL
SELECT COUNT(aa.ASSET_TYPE) AS eValueA, aa.ASSET_TYPE_NAME AS eWordA FROM I_BLUE_ISSUE_ASSET as ibia LEFT JOIN I_BLUE_ISSUE AS ibi on(ibia.eIssueKey = ibi.eIssueKey) LEFT JOIN A_ASSET AS aa on(aa.ASSET_NUM = ibia.eAssetKey) WHERE ibi.eStatus != '삭제' AND aa.ASSET_TYPE IS NOT NULL AND DATE_FORMAT(ibi.eReqDate, '%Y-%m-%d') BETWEEN #topStartDate# AND #topEndDate# GROUP BY aa.ASSET_TYPE UNION ALL
SELECT COUNT(aa.ASSET_TYPE) AS eValueA, aa.ASSET_TYPE_NAME AS eWordA FROM I_BLUE_SR_ASSET as ibsa LEFT JOIN I_BLUE_SR AS ibs on(ibsa.eIssueKey = ibs.eIssueKey) LEFT JOIN A_ASSET AS aa on(aa.ASSET_NUM = ibsa.eAssetKey) WHERE ibs.eStatus != '삭제' AND aa.ASSET_TYPE IS NOT NULL AND DATE_FORMAT(ibs.eReqDate, '%Y-%m-%d') BETWEEN #topStartDate# AND #topEndDate# GROUP BY aa.ASSET_TYPE
) as aa GROUP BY eWordA

/*mesStatisticsDAO.eAssetMakerList*/ SELECT COUNT(eei.ENTRY_ITEM_NUM) AS eValueA ,aa.ASSET_MAKER AS eWordA ,SUM(CASE WHEN eei.eEntryImportDate IS NULL OR eei.eEntryImportDate = '' THEN 1 ELSE 0 END) AS eValueB FROM E_ENTRY_ITEM AS eei LEFT JOIN E_ENTRY AS ee ON (ee.ENTRY_NUM =eei.ENTRY_NUM)
LEFT JOIN A_ASSET AS aa ON (aa.ASSET_NUM =eei.ASSET_NUM)
WHERE ee.ENTRY_STATUS != '삭제' AND aa.ASSET_DEL_STATUS != '삭제' AND aa.ASSET_MAKER != '삭제' AND DATE_FORMAT(ee.eEntryExitDate, '%Y-%m-%d') BETWEEN #topStartDate# AND #topEndDate# GROUP BY aa.ASSET_MAKER

/*mesStatisticsDAO.secondValuList*/ SELECT COUNT(aa.ASSET_TYPE) AS eValueC ,aa.ASSET_TYPE_NAME AS eWordB ,SUM(CASE WHEN eei.eEntryImportDate IS NULL OR eei.eEntryImportDate = '' THEN 1 ELSE 0 END) AS eValueD FROM E_ENTRY_ITEM AS eei LEFT JOIN E_ENTRY AS ee ON (ee.ENTRY_NUM =eei.ENTRY_NUM)
LEFT JOIN A_ASSET AS aa ON (aa.ASSET_NUM =eei.ASSET_NUM)
WHERE ee.ENTRY_STATUS != '삭제' AND aa.ASSET_DEL_STATUS != '삭제' AND DATE_FORMAT(ee.eEntryExitDate, '%Y-%m-%d') BETWEEN #topStartDate# AND #topEndDate# GROUP BY aa.ASSET_TYPE

/*mesStatisticsDAO.selectOutputList*/ SELECT eProjectName AS eWordA, eStartDate AS eWordB, eEndDate AS eWordC, eManager AS eWordD, eProjectPM AS eWordE, ePMContact AS eWordF, COUNT(pdf.eDeliverableFileNUM) eValueD, SUM(CASE WHEN pdf.eFileRowGubun = 'A' THEN 1 ELSE 0 END) AS eValueA, SUM(CASE WHEN pdf.eFileRowGubun = 'B' THEN 1 ELSE 0 END) AS eValueB, SUM(CASE WHEN pdf.eFileRowGubun = 'C' THEN 1 ELSE 0 END) AS eValueC FROM P_PROJECT AS pp LEFT JOIN P_Deliverable_file AS pdf ON pp.P_PROJECT_NUM = pdf.eProjectNum
WHERE eProjectStatus != '삭제' AND ((eStartDate BETWEEN #topStartDate# AND #topEndDate#) OR (eEndDate BETWEEN #topStartDate# AND #topEndDate#))
GROUP BY pp.P_PROJECT_NUM
